#include <bits/stdc++.h> using namespace std; char Map[9][9]; int dir[4][2]={{0,-1},{0,1},{-1,0},{1,0}}; int n,m,t,di,dj,flag; void dfs(int si,int sj,int step) { if(flag) return; if(si==di && sj==dj && step==t) { flag=1; return; } if(si<1 || sj<1 || si>n || sj>m) return; if(abs(si-di)+abs(sj-dj)+step>t)//剪枝 return; for(int i=0;i<4 && !flag;i++) { if(Map[si+dir[i][0]][sj+dir[i][1]]!='X') { Map[si+dir[i][0]][sj+dir[i][1]]='X'; dfs(si+dir[i][0],sj+dir[i][1],step+1); Map[si+dir[i][0]][sj+dir[i][1]]='.'; } } return; } int main() { int si,sj,wall; while(1) { scanf("%d%d%d",&n,&m,&t); if(n==0 && m==0 && t==0) break; wall=0; getchar(); for(int i=1;i<=n;i++) { for(int j=1;j<=m;j++) { scanf("%c",&Map[i][j]); if(Map[i][j]=='S') {si=i;sj=j;} else if(Map[i][j]=='D') {di=i;dj=j;} else if(Map[i][j]=='X') wall++; } getchar(); } if(abs(si+sj-di-dj-t)%2==1 || n*m-wall<=t)//剪枝 { printf("NO
"); continue; } flag=0; Map[si][sj]='X'; dfs(si,sj,0); if(flag==1) printf("YES
"); else printf("NO
"); } return 0; }

